import "./App.css";
import workouts from "./workouts.json";
import sets from "./workoutsets.json";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faDumbbell,
  faPersonRays,
  faListOl,
} from "@fortawesome/free-solid-svg-icons";

function App() {
  return (
    <div>
      <h1 className="title">GenWork</h1>
      <Generator />
    </div>
  );
}

function Generator() {
  const [workoutList, changeWorkoutList] = useState();

  const randomize = () => {
    // Get body parts to workout and num of exercises for each body part

    let num = document.querySelector(".num-exercises").value;
    let count = 0;
    let exercises = [];
    let workout = "";

    let bodyParts = [];
    let bodyList = document.querySelectorAll(".body-part");
    for (let item of bodyList) {
      if (part.checked) {
        bodyParts.push(part.value);
      }
    }

    // Remove any whitespace surrounding each word
    bodyParts = bodyParts.map((part) => part.toLowerCase().trim());

    // Generate random workout
    for (let part of bodyParts) {
      while (count < num) {
        var exercise =
          workouts[part][Math.floor(Math.random() * workouts[part].length)];
        var inside = true;
        console.log(exercise);
        while (inside) {
          if (exercises.includes(exercise)) {
            exercise =
              workouts[part][Math.floor(Math.random() * workouts[part].length)];
          } else {
            exercises.push(exercise);
            inside = false;
          }
        }
        count++;
      }
      let workoutSet = sets.sets[Math.floor(Math.random() * sets.sets.length)];
      workout += exercises.join("\n") + "\n\n" + `${workoutSet}` + "\n\n";
      count = 0;
      exercises = [];
      workoutSet = "";
    }
    changeWorkoutList(workout);

    // Reset input values back to empty
    // document.querySelector(".body-parts").value = "";
    document.querySelector(".num-exercises").value = "";
  };

  return (
    <div id="generator-div">
      <div className="body-exercises">
        <h2 className="form-titles">
          <FontAwesomeIcon icon={faPersonRays} className="form-imgs" /> Body
          Part(s) to Workout
        </h2>

        <label>
          <input type="checkbox" value="abs" className="body-part" />
          Abs
        </label>
        <label>
          <input type="checkbox" value="back" className="body-part" />
          Back
        </label>
        <label>
          <input type="checkbox" value="biceps" className="body-part" />
          Biceps
        </label>
        <label>
          <input type="checkbox" value="chest" className="body-part" />
          Chest
        </label>
        <label>
          <input type="checkbox" value="legs" className="body-part" />
          Legs
        </label>
        <label>
          <input type="checkbox" value="shoulders" className="body-part" />
          Shoulders
        </label>
        <label>
          <input type="checkbox" value="triceps" className="body-part" />
          Triceps
        </label>
      </div>
      <div className="num-exercises">
        <h2 className="form-titles">
          <FontAwesomeIcon icon={faListOl} className="form-imgs" /> Number Of
          Exercises
        </h2>
        <input
          type="number"
          placeholder="1"
          size={1}
          min={1}
          max={8}
          className="num-exercises"
          required
        ></input>
      </div>
      <button onClick={randomize}>
        Generate <FontAwesomeIcon icon={faDumbbell} />
      </button>
      <pre>{workoutList}</pre>
    </div>
  );
}

export default App;
