import "./App.css";
import workouts from "./workouts.json";
import sets from "./workoutsets.json";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDumbbell } from "@fortawesome/free-solid-svg-icons";

function App() {
  return (
    <div>
      <h1 className="title">
        <img src="https://cdn-icons-png.flaticon.com/512/3503/3503343.png" />
        GenWork
      </h1>
      <Generator />
    </div>
  );
}

function Generator() {
  const [workoutList, changeWorkoutList] = useState();

  const randomize = () => {
    // Get body parts to workout and num of exercises for each body part
    let bodyParts = document.querySelector(".body-parts").value;
    let num = document.querySelector(".num-exercises").value;
    let count = 0;
    let exercises = [];
    let workout = "";

    // If there are multiple values split it by the word and
    if (bodyParts.includes("and")) bodyParts = bodyParts.split("and");

    // Remove any whitespace surrounding each word
    bodyParts = bodyParts.map((part) => part.toLowerCase().trim());

    // Generate random workout
    for (let part of bodyParts) {
      while (count < num) {
        var exercise =
          workouts[part][Math.floor(Math.random() * workouts[part].length)];
        var inside = true;
        while (inside) {
          if (exercises.includes(exercise)) {
            exercise =
              workouts[part][Math.floor(Math.random() * workouts[part].length)];
          } else {
            exercises.push(exercise);
            inside = false;
          }
        }
        count++;
      }
      let workoutSet = sets.sets[Math.floor(Math.random() * sets.sets.length)];
      workout += exercises.join("\n") + "\n\n" + `${workoutSet}` + "\n\n";
      count = 0;
      exercises = [];
      workoutSet = "";
    }
    changeWorkoutList(workout);

    // Reset input values back to empty
    document.querySelector(".body-parts").value = "";
    document.querySelector(".num-exercises").value = "";
  };

  return (
    <div id="generator-div">
      <label>
        What Body Part(s) do you want to workout?
        <input
          type="text"
          placeholder="Choose between abs,chest,back,biceps,triceps,shoulders or legs"
          size={55}
          className="body-parts"
          required
        ></input>
      </label>
      <label>
        How many exercises for each body part?
        <input
          type="number"
          placeholder="1"
          size={1}
          min={1}
          max={8}
          className="num-exercises"
          required
        ></input>
      </label>
      <button onClick={randomize}>
        Generate <FontAwesomeIcon icon={faDumbbell} />
      </button>
      <pre>{workoutList}</pre>
    </div>
  );
}

export default App;
