import "./App.css";
import workouts from "./workouts.json";
import { useEffect, useState } from "react";

function App() {
  return (
    <div>
      <h1> Workout Generator</h1>
      <Generator />
    </div>
  );
}

function Generator() {
  const randomize = () => {
    let parts = document.querySelector(".body-parts").value.split("and");
    parts = parts.map((part) => part.toLowerCase().trim());
    console.log(parts);
  };

  return (
    <div id="generator-div">
      <label>
        What Body Part(s) do you want to workout?
        <input
          type="text"
          placeholder="Choose between abs,chest,back,biceps,triceps,shoulders or legs"
          size={55}
          className="body-parts"
        ></input>
      </label>
      <label>
        How many exercises for each body part?
        <input
          type="text"
          placeholder="0"
          size={1}
          maxLength={1}
          className="num-exercises"
        ></input>
      </label>
      <button onClick={randomize}>Randomize</button>
    </div>
  );
}

export default App;
